'use strict';

var buttons = document.querySelectorAll('button');

if (buttons) {
  for (var i = 0; i < buttons.length; i++) {
    buttons[i].addEventListener('mouseup', function () {
      this.blur();
    });
  }
}

;
new Swiper('.slider-sertificate__container', {
  navigation: {
    nextEl: '.swiper-button-next',
    prevEl: '.swiper-button-prev'
  },
  slidesPerView: 3,
  breakpoints: {
    576: {
      slidesPerView: 3,
      spaceBetween: 15
    },
    680: {
      slidesPerView: 3,
      spaceBetween: 15
    },
    768: {
      slidesPerView: 3,
      spaceBetween: 15
    },
    992: {
      slidesPerView: 3,
      spaceBetween: 7
    }
  }
});
;
var nav = document.querySelector('.nav');
var burgerBtn = document.querySelector('.header__burger');
var burgerBtnClose = document.querySelector('.nav__icon-close');
burgerBtn.addEventListener('click', function (evt) {
  evt.preventDefault();
  nav.classList.add('nav--active'); // burgerBtn.classList.remove('header__burger--opened');
});
burgerBtnClose.addEventListener('click', function (evt) {
  evt.preventDefault();
  nav.classList.remove('nav--active');
  burgerBtn.classList.add('header__burger--opened');
});
